class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int c=0;
        int ans=0;
        for(int i=0;i<n-1;i++)
        {
            if(s[i]=='1' && s[i+1]=='0')
            {
                ans+=c+1;
            }
            if(s[i]=='1')
                c++;
        }
        return ans;
    }
};
