class Solution:
    def maxOperations(self, s: str) -> int:
        contiguous0 = 0
        ans = 0
        i = len(s) - 1
        while i >= 0:
            if s[i] == '0':
                contiguous0 += 1
                while i >= 0 and s[i] == '0':
                    i -= 1
                continue
            else:
                ans += contiguous0
                i -= 1
        return ans
