class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        boolean f = true;
        int ans = 0;
        for (int i = s.length() - 1; i >= 0; i--) {
            if (s.charAt(i) == '1') {
                ans += cnt;
                f = true;
            } else if (f) {
                f = false;
                cnt++;
            }
        }
        return ans;
    }
}
