class Solution:
    def maxOperations(self, s: str) -> int:
        arr=''
        for j in range(len(s)):
            if(s[j]=='1'):
                arr+='1'
            else:
                if(arr and arr[-1]!='0'):
                    arr+='0'

        cnt=0
        op=0
        for j in range(len(arr)):
            if(arr[j]=='1'):
                cnt+=1
            else:
                op+=cnt
        return op
