class Solution {
public:
    int maxOperations(string s) {
        int ans=0,n=s.size(),cnt=s[0]=='1';
        char prev=s[0];
        for(int i=1;i<n;i++){
            if(prev=='1' && s[i]=='0'){
                ans+=cnt;
            }
            cnt+=(s[i]=='1');
            prev=s[i];
        }
        return ans;
    }
};
