typedef long long ll;
#define repn(i,n,e) for(ll i = n; i < e; i++)
#define rep(i,n,e) for(ll i = n; i <= e; i++)
#define sor(a)  sort(a.begin(), a.end())
#define pb push_back
#define mod 1000000007
class Solution {
public:

int maxOperations(string s) {
int ans = 0, p = 0;
        bool k = 0;
        vector<int> h;
        int n = s.size();

        repn(i, 0, n) {
            if (s[i] == '1') {
                h.pb(1);
                k = 1;
            }
            if (s[i] == '0' && k) {
                h.pb(0);
                k = 0;
            }
        }

        for (auto it : h) {
            if (it ==1){
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
      }
};
