class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0, n = s.length();

        for (int i = 0; i < n; i++)
        {
            if (s[i] == '1')
                cnt++;
            else if (s[i] == '0' && (i < n - 1 && s[i + 1] == '1') || (i == n - 1))
            {
                ans += cnt;
            }
        }

        return ans;
    }
};
