class Solution {
public:
    int maxOperations(string s) {
        int n= s.size();
        int ans = 0,ones = 0;
        bool zero = false;
        for(int i = 0;i<n;i++){
            if(s[i]=='0'){
                if(zero){
                    zero=false;
                    ans+=ones;
                }
            }
            else{
                if(!zero){
                    zero=true;
                }
                ones++;
            }
        }
        return ans;
    }
};
