class Solution {
public:
    int maxOperations(string s) {
        s += '1';
        int ans = 0, t = 0, n = s.size();
        for (int i = 0; i < n; i ++ ) {
            if (s[i] == '1') {
                int j = i, c = 0;
                while(j < n && s[j] == '1') {
                    ++c;
                    ++j;
                }
                i = j - 1;
                t += c;
                ans += t;
                if (i == n - 1) ans -= t;
            }
        }
        return ans;
    }
};
