class Solution {
public:
    int maxOperations(string s) {
        bool hold=0;
        int ans=0, l=0,r=0, one_count =0;
        for (int i=0;i<s.size();i++) {
            if (s[i]-'0') {
                hold=true; one_count++;
            } else {
                if (hold) {
                    hold = false;
                    ans+=one_count;
                }
            }
        }
        return ans;
    }
};
