class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)

        ans = ones = 0
        i = 0
        for i in range(n):
            if i and s[i] == s[i-1] == '0':
                continue
            if s[i] == '0':
                ans += ones
            else:
                ones += 1
        return ans
