class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int n = s.length();
        int cnt = 0;
        int ind = -1;

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '0') continue;

            if (i - ind > 1) {
                ans += cnt;
            }
            cnt++;
            ind = i;
        }

        if (s.charAt(n - 1) == '0') {
            ans += cnt;
        }

        return ans;
    }
}
