class Solution {
    public int maxOperations(String s) {
        int[] pre=new int[s.length()];
        int cnt=0;
        StringBuilder str=new StringBuilder();
        char prev='.';
        for(int i=0;i<s.length();i++)
        {
            char ch=s.charAt(i);
            if(ch=='1')
            str.append('1');
            else
            {
                if(prev!='0')
                str.append('0');
            }
            prev=ch;
        }
        s=str.toString();
        for(int i=0;i<s.length();i++)
        {
            if(s.charAt(i)=='1')
            cnt++;
            pre[i]=cnt;
        }
        int ans=0;
        for(int i=0;i<s.length();i++)
        {
            if(s.charAt(i)=='0')
            ans+=pre[i];
        }
        return ans;
    }
}
