class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int ops = 0;
        bool zeroFound = false;

        for (char c : s) {
            if (c == '1') {
                if (zeroFound) {
                    ops += ones;
                    zeroFound = false;
                }
                ++ones;
            }
            if (c == '0')
                zeroFound = true;
        }
        if (zeroFound)
            ops += ones;

        return ops;
    }
};
