class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int pre=n;
        vector<int> a;
        for(int i=0;i<n;i++){
            if(s[i]=='1')a.push_back(i);
        }
        int sz=a.size(),res=0;
        for(int i=sz-1;i>=0;i--){
            if(pre-a[i]>1)res+=i+1;
            pre=a[i];
        }
        return res;
    }
};
