class Solution {
    public int maxOperations(String s) {
        //能移就移
        //cnt记录前面的个数
        int n = s.length();
        int ans = 0, cnt = 0;
        for(int i = 0; i < n - 1; i++){
            if(s.charAt(i) == '1'){
                ++cnt;
                if(s.charAt(i + 1) == '0')
                    ans += cnt;
            }
        }
        return ans;
    }
}
