class Solution {
public:
    int maxOperations(string s) {
        if(is_sorted(s.begin(),s.end()))
            return 0;
        int ans=0,lidx=0,n=s.size(),ok=0,ct=0;
        for(int i=0;i<n;++i)
        {
            if(s[i]=='1')
            {
                if(!ok)
                {
                    ok=1;
                }
                else
                {
                    if((i-lidx)>1)
                    {
                        // cout<<ct<<'\n';
                        ans+=ct;
                    }
                }
                lidx=i;
                ++ct;
            }
        }
        // cout<<lidx<<' '<<ans<<' '<<ct<<'\n';
        if(lidx<n-1)
            ans+=ct;
        // cout<<ct<<'\n';
        return ans;

    }
};
