class Solution {
public:
    int maxOperations(string s) {
        int cur1Num = 0;
        int res = 0;
        bool isz = false;
        for(int i=0;i<s.size();i++){
            if(s[i] == '1'){
                if(isz){
                    res += cur1Num;
                    isz = false;
                }
                cur1Num++;
            }
            else{
                isz = true;
            }
        }
        if(isz) res += cur1Num;
        return res;
    }
};
