class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int ones=0;
        ones=s[0]=='1'?1:0;
        for(int i=1;i<s.size();i++)
        {
            if((i==s.size()-1 && s[i]=='0') || (s[i]=='1' && s[i-1]=='0'))
            {
                count+=ones;
            }
            if(s[i]=='1')
            ones++;
        }
        return count;
    }
};
