class Solution {
public:
    int maxOperations(string target) {
        int value = 0;
        int ans2 = 0;
        bool foundOne = false;
        vector<int> nums;
        int i = 0;

        while (i < target.size()) {
            if (target[i] == '1') {
                nums.push_back(1);
                foundOne = true;
            }
            if (target[i] == '0' && foundOne) {
                nums.push_back(0);
                foundOne = false;
            }
            i++;
        }

        int j = 0;
        while (j < nums.size()) {
            if (nums[j] == 1) {
                ans2++;
            } else {
                value += ans2;
            }
            j++;
        }

        return value;
    }
};
