class Solution {
public:
    int maxOperations(string s) {
        function<vector<int>(string&)> solve = [](string& s) {
            vector<int> seq;
            int toggle = 0;
            for (char c : s) {
                if (c == '1') {
                    seq.push_back(1);
                    toggle = 1;
                }
                if (c == '0' && toggle == 1) {
                    seq.push_back(0);
                    toggle = 0;
                }
            }

            return seq;
        };
        function<int(vector<int>&)> solve2 = [](vector<int>& seq) {
            int res = 0, count1 = 0;
            for (int bit : seq) {
                if (!bit) {
                    res += count1;
                } else {
                    count1++;
                }
            }

            return res;
        };
        vector<int> seq = solve(s);
        return solve2(seq);
    }
};
