class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ones = []

        pre = 0
        for i, c in enumerate(s):
            if c == '1':
                pre += 1
            else:
                if pre > 0:
                    ones.append(pre)
                    pre = 0

        result = 0
        t = 0
        for num in ones:
            result += t
            t += num
        result += t
        return result
