class Solution {
public:
    int maxOperations(string s) {
        vector<int> vec;
        bool check=false;
        int a=0,b=0;
        for(int j=0;j<s.size();j++){
            if(s[j]=='1'){
                vec.push_back(1);check=true;
            }
            if((s[j]=='0')&&(check))
            {
                vec.push_back(0);check=false;
            }
        }
        for(auto loop:vec){
            if(loop){
                b++;
            }
            else{
                a+=b;
            }
        }
        return a;

    }
};
