class Solution {
public:
    int maxOperations(string s) {
        int t = 0;
        int c = 0;
        char l = '\0';
        for(int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                if (l == '1') {
                    c += 1;
                }
                else {
                    t += c;
                    c += 1;
                }
                l = '1';
            }
            else {
                l = '0';
                if (i == s.size() - 1) {
                    t += c;
                }
            }
        }

        return t;
    }
};
