class Solution {
public:
    int maxOperations(string s) {
        stack <char> st;
        int cnt=0;
        int n=s.size();
        if(s[0]=='1'){
            st.push(s[0]);
        }
        for(int i=1;i<n;i++){
            if(s[i]=='1'){
                if(s[i-1]=='0'){
                    cnt+=st.size();
                    st.push(s[i]);
                }
                else{
                    st.push(s[i]);
                }
            }


        }
        if(s[n-1]=='0'){
            cnt+=st.size();
        }
        return cnt;
    }
};
