class Solution:
    def maxOperations(self, s: str) -> int:
        dp = [0] * len(s)
        for i in range(len(s) - 2, -1 , -1):
            if s[i] == "0":
                dp[i] = dp[i+1]
            else:
                if s[i+1] == "1":
                    dp[i] = dp[i+1]
                else:
                    dp[i]= dp[i+1] + 1
        ans = 0
        for i in range(len(s)):
            ans += int(s[i]) * dp[i]
        return ans
