class Solution {
    public int maxOperations(String s) {
        int ops = 0;
        List<Integer> lst = new ArrayList<>();
        int oneCount = 0;
        for(int i = 0; i < s.length(); i++) {
            char ch = s.charAt(i);
            if(ch == '1')
                oneCount++;
            else {
                if(oneCount != 0)
                    lst.add(oneCount);
                oneCount = 0;
            }
        }
        int mult = lst.size();
        for(int i : lst) {
            ops += (i*mult);
            mult--;
        }
        return ops;
    }
}
