class Solution {
public:
    int maxOperations(string s) {
        int sol=0, n=0;
        bool check = true;
        for(int i=0; i<s.size(); i++){
            if(s[i]=='1'){
                n++;
                check=true;
            }
            else{
                if(check)sol+=n;
                check=false;
            }

        }
        return sol;

    }
};
