class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), cnt = 0, ans = 0;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '1') {
                ans += cnt;
            }
            else if (s[i] == '0' && (i + 1 >= n || s[i + 1] == '1')) {
                cnt++;
            }
        }
        return ans;
    }
};
