class Solution {
    public int maxOperations(String s) {

        int op = 0, one = 0;
        boolean prev = false;

        for (char c : s.toCharArray()) {
            if (c == '1') {
                one++;
                prev = true;
            } else if (c == '0' && prev) {
                op += one;
                prev = false;
            }
        }

        return op;
    }
}
