class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int i = n-1;
        while(i>=0&&s.charAt(i)=='1') i--;
        int ct = 0;
        int add = 0;
        int ans = 0;
        while(i>=0){
            if(s.charAt(i)=='1'){
                ans+=add;
                i--;
            }else{
                 while(i>=0&&s.charAt(i)=='0') i--;
                 add++;
            }
        }
        return ans;
    }
}
