class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        arr = []
        i = 0
        while i < n:
            count = 0
            while i < n and s[i] == '1':
                count += 1
                i += 1
            if count > 0:
                arr.append(count)
            else:
                i += 1
        if s[-1] == '0':
            arr.append(1)
        ans = 0
        n = len(arr)
        i = 0
        while i + 1 < n:
            ans += arr[i]
            arr[i + 1] += arr[i]
            i += 1
        return ans
