class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int sum = 0;
        s = s + '1';
        for(int i = 0; i < s.size() - 1; i++){
            if(s[i] == '1'){
                sum++;
            }
            if(s[i] == '0' && s[i + 1] == '1'){
                res += sum;
            }
        }
        return res;
    }
};
