class Solution {
public:
    int maxOperations(string str) {
        int result = 0, countOnes = 0, zeroFlag = 0, i = 0;
        bool oneDetected = false;
        vector<int> sequence;

        while (i < str.size()) {
            if (str[i] == '1') {
                sequence.push_back(1);
                oneDetected = true;
            }
            if (str[i] == '0' && oneDetected) {
                sequence.push_back(0);
                oneDetected = false;
            }
            ++i;
        }

        i = 0;

        while (i < sequence.size()) {
            if (sequence[i] == 1) {
                countOnes++;
            } else {
                result += countOnes;
            }
            ++i;
        }

        return result;
    }
};
