class Solution {
public:
    int maxOperations(string s) {
        bool flag = 0; 
        vector<int> vec; 

        for(int i=0;i<s.size();i++){ 
            if(s[i] == '1'){ 
                vec.push_back(1); 
                flag = 1; 
            } 
            if(s[i] == '0' && flag == 1){ 
                vec.push_back(0); 
                flag = 0; 
            } 
        } 

        int ans = 0, cnt = 0;
        for(auto it: vec){ 
            if(it == 1) cnt++; 
            else ans += cnt; 
        } 
        return ans; 
    }
};
