class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int num1=0, ans=0;
        bool move = false;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                num1++;
                move = false;
            }else if(s[i]=='0'){
                if(!move){
                    ans+=num1;
                    move= true;
                }
            }
        }
        return ans;
    }
};
