class Solution {
public:
    int maxOperations(string s) 
    {
        string s1="";
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1')
            {
                s1+='1';
            }else{
                if(s1.length() && s1.back()!='0')
                {
                    s1+='0';
                }
            }
        }
        s=s1;
        int sum=0,ones=0;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1')
            {
                ones++;
            }else{
                sum+=ones;
            }
        }
        return sum;

    }
};
