class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ans=0,ones=0,flag=0;
        for(int i=0;i<n;i++){
            if(s[i]=='0')flag=1;
            if(flag==1 && s[i]=='1'){
                ans+=ones;
            }
            if(s[i]=='1'){
                ones++;
                flag=0;
            }
        }
        if(s[n-1]=='0')ans+=ones;
        return ans;
    }
};
