class Solution
{
public:
    int maxOperations(string s)
    {
        int n = s.length();
        int ans = 0;
        int cnt = 0;
        int flag = 0;
        for (int i = 0; i < n; i++)
        {
            if (s[i] == '1')
            {
                cnt++;
                flag = 1;
            }
            else if (flag)
            {
                ans += cnt;
                flag = 0;
            }
        }
        return ans;
    }
};
