class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int l = 0;
        while (l < n && s[l] == '0') l++;
        int r = n - 1;
        while (r >= 0 && s[r] == '1') r--;
        while (r >= 0 && s[r] == '0') r--;
        if (r < l) return 0;
        int ans = 0;
        int acc = 0;
        while (l <= r) {
            int j = l + 1;
            while (j < n && s[j] == '1') j++;
            acc += j - l;
            ans += acc;
            while (j < n && s[j] == '0') j++;
            l = j;
        }
        return ans;
    }
};
