class Solution {
    public int maxOperations(String s) {
        int one=0,zero=0;
        int ans=0;
        for(char c : s.toCharArray()){
            if(c=='1'){
                one++;
                zero=0;
            }else{
                zero++;
                if(zero==1){
                    ans+=one;
                }       
            }
        }
        return ans;
    }
}
