class Solution {
public:
    int maxOperations(string s) {
            int op = 0, n = s.length(), cnt = 0;
            bool flag = false;
            for(int i = 0; i < n; i++){
                    if(s[i] == '1'){
                            if(flag){
                                    flag = false;
                                    op+=cnt;
                            }
                            cnt++;
                    }
                    else flag = true;
            }
            if(s[n-1] == '0'){
                   op+= cnt; 
            }
            return op;

    }
};
