class Solution {
public:
    int maxOperations(string s) {
        queue<int>pq;
        s.push_back('1');
        int i=0,res = 0,tmp = 0;
        while(i < s.size()){
            int j = i;
            while( j<s.size() && s[j] == '0') j++;
            int cnt = 0;
            while(j < s.size() && s[j] == '1') {cnt++;j++;}
            if(cnt > 0) pq.push(cnt);
            i = j;
        }

        while(!pq.empty()){
            auto p = pq.front();
            pq.pop();
            res += tmp;
            tmp += p;
        }
        return res;
    }
};
