class Solution {
public:
    vector<int>v;
    int maxOperations(string s) {
        int n=s.size();
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                int cnt=0;
                int j=i,flag=0;
                while(s[j]=='1')
                {
                    cnt++;
                    j++;
                    if(j==n)
                    {
                        flag=1;
                    }
                }
                i=j;
                if(flag==0) v.push_back(cnt);
            }
        }
        int ans=0;
        int cnt=1;
        for(int i=v.size()-1;i>=0;i--)
        {
            ans+=v[i]*cnt;
            cnt++;
        }
        return ans;
    }
};
