class Solution {
public:
    int maxOperations(string s) {
        string k="";
        k+=s[0];
        for(int i=1;i<s.size();i++) 
        {
            if(s[i-1]=='0'&&s[i]=='0') continue;
            k+=s[i];
        }
        int sum=0,c=0;
        for(int i=0;i<k.size();i++)
        {
            if(k[i]=='0') sum+=c;
            else c++;
        }
        cout<<k;
        return sum;
    }
};
