class Solution:
    def maxOperations(self, s: str) -> int:
        s = list(s)
        cur = 1
        for i in range(len(s)):
            if s[i] == '1':
                s[i] = cur
                cur += 1
            else:
                s[i] = 0
        isZero = False
        total = 0
        for i in range(len(s) -1, -1, -1):
            if s[i] == 0:
                isZero = True
            else:
                if isZero:
                    total += s[i]
                    isZero = False
        return total
