class Solution {
    public int maxOperations(String s) {
        char[] cs = s.toCharArray();
        int n = s.length();
        int idx = 0;
        int tL = 0;

        List<Integer> res = new ArrayList<>();

        res.add(tL);
        while(idx<n){
            if(cs[idx] == '1'){
                tL++;
            } else {
                if(tL != 0){
                    res.add(tL+res.get(res.size()-1));
                    tL = 0;
                }
            }
            idx++;
        }

        int ans = 0;
        for(int x:res){
            ans+=x;
        }
        return ans;
    }
}
