class Solution:
    def maxOperations(self, s: str) -> int:
        count = 0
        oc = 0

        for i in range(len(s)):
            if s[i] == '1':
                oc += 1
                continue

            if i != 0 and s[i - 1] == '0':
                continue

            count += oc

        return count
