class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int countOnesSoFar = 0;
        int lastOneIndex = -1;
        int n = s.length();
        for(int i=0;i<n;i++){
            char c = s.charAt(i);
            if( c == '1'){
                if(lastOneIndex != -1 && lastOneIndex+1 != i){
                    ans += countOnesSoFar;
                }

                lastOneIndex = i;
                countOnesSoFar++;
            } else if(i==n-1 && lastOneIndex != -1 ){
                ans += countOnesSoFar;
            }
        }
        return ans;
    }
}
