class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), ans = 0, cnt = 0;
        vector<int> R(n, 0);
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == '0') {
                cnt++;
                while (i - 1 >= 0 && s[i - 1] == '0') i--;
            } else {
                R[i] = cnt;
            }
        }
        int p = -1;
        for (int i = n - 2; i >= 0; i--) if (s[i] == '1' && s[i + 1] == '0') {
            ans += R[i];
            p = i;
            break;
        }
        for (int i = p - 1; i >= 0; i--) if (s[i] == '1') ans += R[i];
        return ans;
    }
};
